import React from 'react';
import ReactDOM from 'react-dom';
import classes from './ErrorModal.module.css'
import Card from "./Card";
import Button from "./Button";

const ErrorModal = (props) => {
	const Backdrop = (props) => {
		return (
				<div className={classes.backdrop} onClick={props.onClickCloseBTN}>
				</div>
		)
	}
	const Overlay = (props) => {
		return (
				<Card className={classes.modal}>
					<header className={classes.header}>
						<h2>{props.title}</h2>
					</header>
					<div className={classes.content}>
						<p>{props.message}</p>
					</div>
					<footer className={classes.actions}>
						<Button onClick={props.onClickCloseBTN}>닫기</Button>
					</footer>
				</Card>
		)
	}
	const backdropRoot = document.getElementById('backdrop_root');
	const overlayRoot = document.getElementById('overlay_root');
	return (
			<React.Fragment>
				{ReactDOM.createPortal(<Backdrop onClick={props.onClickCloseBTN} />, backdropRoot)}
				{ReactDOM.createPortal(<Overlay onClick={props.onClickCloseBTN} />, overlayRoot)}
			</React.Fragment>
	)
}
export default ErrorModal;
