import React, {useState} from 'react';
import AddMember from './components/Members/AddMember'
import MemberList from "./components/Members/MemberList";

const DUMMY_MEMBERS = [{
	id: 'member01',
	name: 'vitamin01',
	age: 1,
}, {
	id: 'member02',
	name: 'vitamin02',
	age: 2,
}, {
	id: 'member03',
	name: 'vitamin03',
	age: 3,
}]

function App() {
	const [memberList, setMemberList] = useState([DUMMY_MEMBERS])
	const addMemberHandler = (memberName, memberAge) => {
		setMemberList((prevMemberList) => {
			return [
				...prevMemberList, // 이전 회원 정보 출력
				{name: memberName, age: memberAge, id: Math.random().toString()}, //새로운 회원 정보 출력
			]
		})
	}

	return (
			<div>
				<AddMember onAddMember={addMemberHandler}/>
				<MemberList members={memberList}/>
			</div>
	);
}

export default App;
